css:
.triangle-with-partial-borders {
  border: 1px solid #333;
  padding: 1.5em;
  position: relative;
  width: 50px;
}
.triangle-with-partial-borders:before,
.triangle-with-partial-borders:after {
  content: "";
  display: block;
  height: 0; 
  left: 50%;
  position: absolute;
  width: 0; 
}
.triangle-with-partial-borders:before {
  border-left: 8px solid transparent;
  border-right: 8px solid transparent;
  border-bottom: 6px solid red;
  margin-left: -5px;
  top: -6px;
  z-index: 4;
}
.triangle-with-partial-borders:after {
  border-left: 6px solid transparent;
  border-right: 6px solid transparent;
  border-bottom: 5px solid #fff;
  margin-left: -3px;
  top: -5px;
  z-index: 5;
}

